    /**
     * 滑动窗口法：
     * 1.定义最左left
     * 2.遍历字符，判断位置
     * 3.如果有并且left还没到达，将left位置挪到已存在位置+1；如果已经过了，则跳过
     * 4.计算最大长度
     * @param s
     * @return
     */
    private static int lengthOfLongestSubstring(String s) {
        int left = 0;// 起始游标
        int maxLength = 0;
        Map<Character, Integer> map = new HashMap<>();
        for (int endIndex = 0; endIndex < s.length(); endIndex++) {
            char a = s.charAt(endIndex);
            if (map.containsKey(a)){
                // 已存在
                left = map.get(a) + 1 > left ? map.get(a) + 1 : left;
            }
            map.put(a, endIndex);
            // 计算当前最大值
            int l = endIndex - left + 1;
            if (l > maxLength) {
                maxLength = l;
            }
        }
        return maxLength;
    }

    /**
     * 通过set集合存储字符来判断
     * @param s
     * @return
     */
    private static int lengthOfLongestSubstring2(String s) {
        if(s.length() == 0){
            return 0;
        }else if(s.length() == 1){
            return 1;
        }
        int beginIndex = 0;// 起始游标
        int endIndex = beginIndex;// 最大游标
        int maxLength = 0;
        Set<Character> set = new HashSet<>();
        while (endIndex < s.length()){
            char a = s.charAt(endIndex);
            if (!set.contains(a)){
                // 无重复 放入map 继续下一次
                set.add(a);
                endIndex++;
                if(set.size() >= maxLength){
                    // 判断是不是最长的
                    maxLength = set.size();
                }
            }else{
                if(set.size() >= maxLength){
                    // 判断是不是最长的
                    maxLength = set.size();
                }
                // 已存在
                set.clear();
                endIndex = ++beginIndex;
            }
        }
        return maxLength;
    }
