    /**
     * 最牛逼的解法
     * @param s
     * @return
     */
    public static int lengthOfLongestSubstring5(String s) {
        int n = s.length(), ans = 0;
        Map<Character, Integer> map = new HashMap<>(); // current index of character
        // try to extend the range [i, j]
        for (int j = 0, i = 0; j < n; j++) {
            if (map.containsKey(s.charAt(j))) {
                i = Math.max(map.get(s.charAt(j)), i);
            }
            ans = Math.max(ans, j - i + 1);
            map.put(s.charAt(j), j + 1);
        }
        return ans;
    }

    public static int lengthOfLongestSubstring4(String s) {
        int length = 0;
        if ("".equals(s)) {// 空串直接返回0
            return length;
        }
        int sLength = s.length();
        for (int i = 0; i < sLength; i++) {
            // s的最大不重复子串长度为s.length()，最小长度为1
            for (int j = i + 1; j < sLength; j++) {
                String subStr = s.substring(i, j);
                boolean res = lengthOfLongestSubstring4Sub(subStr);
                if (res) {
                    if (subStr.length() > length) {
                        length = subStr.length();
                    }
                }
            }
        }
        return length;
    }
    public static boolean lengthOfLongestSubstring4Sub(String s){
        if("".equals(s)) return false;
        char[] chars = s.toCharArray();
        Set<Character> set = new HashSet<>();
        for (char c : chars) {
            set.add(c);
        }
        if (set.size() != s.length()) {
            return false;
        }else{
            return true;
        }
    }


    /**
     * 长字符串优化明显，但还是超时
     * @param s
     * @return
     */
    public static int lengthOfLongestSubstring3(String s) {
        int length = 0;
        if ("".equals(s)) {
            return length;
        }
        char[] chars = s.toCharArray();
        int sLength = chars.length;
        StringBuilder maxStr = new StringBuilder(sLength);
        for (int i = 0; i < sLength; i++) {
            maxStr.delete(0, maxStr.length());
            char[] reChars = new char[chars.length - 1];
            for (int j = 0; j < chars.length; j++) {
                char c = chars[j];
                String strC = String.valueOf(c);
                if(j > 0) reChars[j-1] = c;
                if (maxStr.indexOf(strC) > 0) {
                    // list包含了此字符
                    if (maxStr.length() > length) {
                        length = maxStr.length();
                    }
                    maxStr.delete(0, maxStr.length());
                }else{
                    // 还没有此字符
                }
                maxStr.append(strC);
            }
            if (maxStr.length() > length) {
                length = maxStr.length();
                // System.out.println(length);
            }
            // s = new String(reChars);
            chars = reChars;
        }

        return length;
    }

    /**
     * 通过字符数组优化，耗时40ms左右
     * @param s
     * @return
     */
    public static int lengthOfLongestSubstring2(String s) {
        int length = 0;
        if ("".equals(s)) {
            return length;
        }

        char[] chars = s.toCharArray();
        int sLength = chars.length;
        for (int i = 0; i < sLength; i++) {
            List<Character> charList = new LinkedList<>();
            char[] reChars = new char[chars.length - 1];
            for (int j = 0; j < chars.length; j++) {
                char c = chars[j];
                if(j > 0) reChars[j-1] = c;
                if (charList.contains(c)) {
                // if (Arrays.binarySearch(chars, c) > 0) {
                    // list包含了此字符
                    if (charList.size() > length) {
                        length = charList.size();
                    }
                    charList = new LinkedList<>();
                }else{
                    // 还没有此字符
                }
                charList.add(c);
            }
            if (charList.size() > length) {
                length = charList.size();
                // System.out.println(length);
            }
            // s = new String(reChars);
            chars = reChars;
        }

        return length;
    }

    /**
     * 原始版本，耗时 150ms左右
     * @param s
     * @return
     */
    public static int lengthOfLongestSubstring(String s) {
        int length = 0;
        if ("".equals(s)) {
            return length;
        }
        int sLength = s.length();
        for (int i = 0; i < sLength; i++) {
            String[] strs = s.split("");
            List<String> charList = new LinkedList<>();
            for (String c : strs) {
                if (charList.contains(c)) {
                    // list包含了此字符
                    if (charList.size() > length) {
                        length = charList.size();
                    }
                    charList = new LinkedList<>();
                }else{
                    // 还没有此字符
                }
                charList.add(c);
            }
            if (charList.size() > length) {
                length = charList.size();
                // System.out.println(length);
            }
            s = s.substring(1);
        }

        return length;
    }
